INSERT INTO `us_editor_sessions` VALUES (52,88,6,'接口文档测试','// 保存会话内容（Ctrl+S调用）\nconst handleSaveSession = async () => {\n  if (!sessionData.value || isSaving.value) return\n\n  if (!validateContentLength(content.value)) {\n    ElMessage.error(\'内容长度超出限制（最大50000字符）\')\n    return\n  }\n\n  isSaving.value = true\n\n  try {\n    await updateSession(sessionData.value.id, {\n      content: content.value,\n      title: sessionData.value.title || generateDefaultTitle(content.value)\n    })\n\n    // 更新本地状态\n    sessionData.value.content = content.value\n    if (!sessionData.value.title || sessionData.value.title === \'未命名文档\') {\n      sessionData.value.title = generateDefaultTitle(content.value)\n    }\n    \n    lastSavedContent.value = content.value\n    hasUnsavedChanges.value = false\n    \n    ElMessage.success(\'内容已保存\')\n\n  } catch (error) {\n    console.error(\'保存会话失败:\', error)\n    ElMessage.error(\'保存失败\')\n  } finally {\n    isSaving.value = false\n  }\n}',0,'edit_document','2025-10-25 07:07:06','2025-10-25 07:07:12');
